import React, { Component } from "react";
import ReviewService from "../services/review.services";
import { buildStyles, CircularProgressbar } from "react-circular-progressbar";
import { Link } from "react-router-dom";

export default class MiniatureList extends Component {
  constructor(props) {
    this.retrieveMiniatures = this.retrieveMiniatures.bind(this);
    this.state = {
      reviews: [],
    };
  }

  componentDidMount() {
    this.retrieveMiniatures;
  }

  retrieveMiniatures() {
    ReviewService.getAll()
      .then((res) => {
        this.setState({ reviews: res.data });
        console.log(res.data);
      })
      .catch((e) => {
        console.log(e);
      });
  }

  render() {
    const { reviews } = this.state;

    return (
      <div className="miniature-container">
        <Link
          to={"/review/" + review.id}
          style={{ textDecoration: "none" }}
          className="link"
        >
          <div className="review">
            <div className="title-date-comments">
              <p>{review.Title}</p>
              <div className="date-comments">
                <p>{review.Date}</p>
                <p>{review.Comments}</p>
              </div>
            </div>

            <div className="rating-image">
              <div className="vl"></div>
              <div className="rating">
                <CircularProgressbar
                  value={review.Rating}
                  styles={buildStyles({
                    textSize: "40px",
                    textColor: "black",
                    pathColor: "#eb3c2d",
                  })}
                  strokeWidth={8}
                  maxValue={10}
                  text={`${review.Rating}`}
                />
              </div>

              <div className="image">
                <img src={"https://picsum.photos/150/100"} alt="example" />
              </div>
            </div>
          </div>
        </Link>
      </div>
    );
  }
}
