            className="form-control"
            id="platform"
            required
            value={this.state.platform}
            onChange={this.onChangePlatform}
            name="platform"
          />
        </div>
        <div className="form-group">
          <label htmlFor="image">Reasoning for rating</label>
          <input
            type="text"
            className="form-control"
            id="image"
            required
            value={this.state.image}
