    super(props);
    this.retrieveReview = this.retrieveReview.bind(this);
    this.state = {
      review: {},
    };
  }

  componentDidMount() {
    this.retrieveReview();
  }

  retrieveReview() {
    ReviewService.get(test)
      .then((response) => {
        this.setState({
          review: response.data,
        });
        console.log(response.data);
      })
      .catch((e) => {
        console.log(e);
      });
  }

  render() {
    const { review } = this.state;
